mapper.select:
select * from ( select rownum RNUM, zxcvb.* from (
select b_no AS "b_no", subject AS "subject", writer AS "writer", content AS "content", to_char(reg_date, 'YYYY-MM-DD') AS "reg_date", readcount AS "readcount" FROM noticeboard order by b_no desc
) zxcvb) where RNUM >= #{begin_rowNo} and RNUM <= #{end_rowNo}

select b_no "b_no" ,subject "subject" ,writer "writer" ,reg_date "reg_date" ,readcount "readcount" ,content "content"
from noticeboard
where b_no=#{b_no}

mapper.update:
      update noticeboard set

      subject = #{subject}
      ,writer = #{writer}
      ,content
      =
      #{content}
      , reg_date = SYSDATE

      where b_no = #{b_no}

mapper.select:
select * from ( select rownum RNUM, zxcvb.* from (
select f.b_no AS "b_no", f.subject AS "subject", f.writer as "writer", -- NICKNAME 대문자를 소문자로 변경금지! ( select nickname from member where m_no = f.writer ) as "NICKNAME",
f.content AS "content", to_char(f.reg_date, 'YYYY-MM-DD') AS "reg_date", f.readcount AS "readcount" FROM freeboard f order by b_no desc
) zxcvb) where RNUM >= #{begin_rowNo} and RNUM <= #{end_rowNo}

select f.b_no "b_no" ,f.subject "subject" ,f.writer "writer" ,( select nickname from member where m_no = f.writer ) as "nickname" ,f.reg_date "reg_date" ,f.readcount "readcount" ,f.content "content"
from freeboard f
where b_no=#{b_no}

select b_no "b_no" , subject "subject" , ( select m_no from member where m_no = ( select writer from freeboard where b_no = #{ b_no } ) ) as "writer" , ( select nickname from member where m_no = ( select writer from freeboard where b_no = #{ b_no } ) ) as "nickname" , content "content" , readcount "readcount" , reg_date "reg_date"
from freeboard
where b_no = #{ b_no }

select count(*)
from freeboard_comment
where 1 = 1 and b_no = #{ b_no }

select * from ( select rownum rnum , zxcvb.*
from (
select comment_no as "comment_no" , b_no as "b_no" , nick_name as "nick_name" , content as "content" , print_no as "print_no" , print_level as "print_level" , to_char(reg_date, 'yyyy-mm-dd hh24:mi:ss') as "reg_date"
from freeboard_comment
where 1 = 1 and b_no = #{ b_no }
order by group_no desc, print_no asc
) zxcvb
where rownum <= ${ end_rowNo } )
where rnum >= ${ begin_rowNo }

mapper.update:
update freeboard
set readcount = readcount + 1
where b_no = #{ b_no }

mapper.select:

select    count(*) 

from    freeboard 

where    b_no = #{ b_no }

