		where		comment_no 	=	${ comment_no }

mapper.select:
select count(*) from ( select * from ( select ima.b_no , ima.subject , ima.writer -- NICKNAME 대문자를 소문자로 변경금지! , ( select nickname from member where m_no = ima.writer ) as "NICKNAME"
, ima.content , ima.readcount , ima.reg_date
from imageboard ima )
where 1 = 1
)

select * from ( select rownum RNUM, zxcvb.* from (
select * from ( select ima.b_no , ima.subject , ima.writer -- NICKNAME 대문자를 소문자로 변경금지! , ( select nickname from member where m_no = ima.writer ) as "NICKNAME"
, ima.content , ima.readcount , to_char(ima.reg_date,'yyyy-mm-dd') reg_date
from imageboard ima )
where 1 = 1
order by reg_date desc, b_no desc
) zxcvb) where RNUM >= #{begin_rowNo} and RNUM <= #{end_rowNo}

mapper.insert:
insert into imageboard ( b_no , subject , writer , content , readcount , reg_date ) values ( ( select nvl( max( b_no ), 0 ) + 1 from imageboard ) , #{ subject }
-- ** 테이블 IMAGEBOARD 테이블 변경시 nickname 부분 수정해야함. , ( select m_no from member where nickname = '${ nickname }' ) --, '${ nickname }'
, #{ content } , 0 , sysdate )

mapper.update:
		update IMAGEBOARD 
      	set
			  subject = #{ subject }
			, content= #{ content }
			, reg_date = SYSDATE

		where b_no = #{ b_no }

mapper.select:
select count(*) from ( select * from ( select s.sale_no , s.subject , s.writer as "writer" -- NICKNAME 대문자를 소문자로 변경금지! , ( select nickname from member where m_no = s.writer ) as "NICKNAME"
, s.content , s.readcount , s.reg_date
from sale s
)
where 1 = 1
)

select count(*) from ( select * from ( select f.free_sharing_no , f.subject , f.writer as "writer" -- NICKNAME 대문자를 소문자로 변경금지! , ( select nickname from member where m_no = f.writer ) as "NICKNAME"
, f.content , f.readcount , f.reg_date , 'free_sharing' as "table_name"
from free_sharing f
)
where 1 = 1
)

select * from ( select rownum RNUM, zxcvb.* from (
select * from ( select s.sale_no as "b_no" , s.subject , s.writer as "writer" -- NICKNAME 대문자를 소문자로 변경금지! , ( select nickname from member where m_no = s.writer ) as "NICKNAME"
, s.content , s.readcount , to_char(s.reg_date,'yyyy-mm-dd') reg_date , 'sale' as "table_name"
from sale s
)
where 1 = 1
order by reg_date desc, "b_no" desc
) zxcvb) where RNUM >= #{begin_rowNo} and RNUM <= #{end_rowNo}

select * from ( select rownum RNUM, zxcvb.* from (
select * from ( select f.free_sharing_no as "b_no" , f.subject , f.writer as "writer" -- NICKNAME 대문자를 소문자로 변경금지! , ( select nickname from member where m_no = f.writer ) as "NICKNAME"
, f.content , f.readcount ,to_char(f.reg_date,'yyyy-mm-dd') reg_date , 'free_sharing' as "table_name"
from free_sharing f
)
where 1 = 1
order by reg_date desc, "b_no" desc
) zxcvb) where RNUM >= #{begin_rowNo} and RNUM <= #{end_rowNo}

mapper.update:
      update sale set

      subject = #{subject}
      ,content= #{content}
      ,reg_date = SYSDATE

      where sale_no = #{b_no}

      update free_sharing set

      subject = #{subject}
      ,content= #{content}
      ,reg_date = sysdate

      where free_sharing_no = #{b_no}

update sale 
       set readcount = readcount +1 
 where sale_no = #{b_no}

update free_sharing 
       set readcount = readcount +1 
 where free_sharing_no = #{b_no}
