mapper.insert:
insert into member(
			m_no
			,name
			,mid
			,password
			,nickname
			,email
			,birthdate
			,phone
			,gender
			,sido_id
			,sigungu_id
			,detail_address
		)
values(
	(select nvl(max(m_no),0)+1 from member)
	,#{name}
	,#{mid}
	,#{password}
	,#{nickname}
	,#{email}
	,to_date(#{birthday},'YYYY-MM-DD')
	,#{phone}
	,#{gender}
	,#{sido_id}
	,#{sigungu_id}
	,#{detail_address}
)

insert into member_detail(
	member
)
values(
	(select max(m_no) from member)
)

insert into player_record(
	player
)
values(
	(select max(m_no) from member)
)

mapper.update:
update
	member
set
	password = #{password}
	,email = #{email}
	,phone = #{phone}
	,sido_id = #{sido_id}
	,sigungu_id = #{sigungu_id}
	,detail_address = #{detail_address}
	,nickname = #{nickname}
where
	mid = #{mid}

mapper.insert:
insert into team(
	team_no
	,team_name
	,team_master
)
values(
	(select nvl(max(team_no),0)+1 from team)
	,#{team_name}
	,#{team_master}
)

insert into team_member(
	team_no
	,team_member
)
values(
	(select team_no from team where team_master = #{team_master})
	,#{team_master}
)
