mapper.select:
select * from ( select rownum RNUM, zxcvb.* from( select distinct(r.b_no) ,r.title ,m.nickname ,r.content ,r.readcount ,r.team_mem ,to_char(r.reg_date, 'YYYY-MM-DD') as reg_date from recruit_TeamMemBoard r LEFT JOIN teammemboard_day d ON r.b_no = d.b_no LEFT JOIN teammemboard_time t ON r.b_no = t.b_no LEFT JOIN member m ON r.writer = m.m_no where 1=1
order by r.b_no desc
order by ${sort_date}
) zxcvb) where RNUM >= #{begin_rowNo} and RNUM <= #{end_rowNo}

mapper.insert:
insert into recruit_teammemboard (b_no, title, writer, content, team_mem,sido,sigungu,pos)
values(
   (select nvl(max(b_no),0)+1 from recruit_teammemboard)
   ,#{title}
   ,#{writer}
   ,#{content}
   ,#{team_mem}
   ,#{sido}
   ,#{sigungu}
   ,#{pos}
)

mapper.select:
select * from ( select rownum RNUM, zxcvb.* from (
select distinct(h.recruitment_no), h.title, m.nickname, h.content, h.readcount, to_char(h.reg_date, 'YYYY-MM-DD') as reg_date from hired_recruitment h LEFT JOIN hiredboard_day dh ON h.recruitment_no = dh.recruitment_no LEFT JOIN hiredboard_time th ON h.recruitment_no = th.recruitment_no LEFT JOIN member m ON h.nickname = m.m_no where 1=1
order by h.recruitment_no desc
order by ${sort_date}
) zxcvb) where RNUM >= #{begin_rowNo} and RNUM <= #{end_rowNo}

mapper.insert:
insert into hired_recruitment (recruitment_no, title, nickname, content, sido, sigungu, pos)
values(
   (select nvl(max(recruitment_no),0)+1 from hired_recruitment)
   
   ,#{title}
   ,#{nickname}
   ,#{content}
   ,#{sido}
   ,#{sigungu}
   ,#{pos}
)

mapper.select:
select * from ( select rownum RNUM, zxcvb.* from (
select distinct(l.recruitment_no) ,l.title ,m.nickname ,l.content ,l.readcount ,l.money ,to_char(l.reg_date, 'YYYY-MM-DD') as reg_date from Lesson_recruitment l LEFT JOIN lessonboard_day dl ON l.recruitment_no = dl.recruitment_no LEFT JOIN lessonboard_time tl ON l.recruitment_no = tl.recruitment_no LEFT JOIN member m ON l.nickname = m.m_no where 1=1
order by recruitment_no desc
order by ${sort_date}
) zxcvb) where RNUM >= #{begin_rowNo} and RNUM <= #{end_rowNo}

mapper.insert:
insert into lesson_recruitment (recruitment_no, title, nickname, content, money, sido, sigungu)
values(
   (select nvl(max(recruitment_no),0)+1 from lesson_recruitment)
   ,#{title}
   ,#{nickname}
   ,#{content}
   ,#{money}
   ,#{sido}
   ,#{sigungu}
)
