mapper.select:
select * from ( select rownum RNUM, zxcvb.* from ( select st.stadium_no AS "stadium_no" ,st.stadium_name AS "stadium_name" ,s.name AS "sido_name" ,si.name as "sigungu_name" ,st.detailed_address as "detail_address" ,to_char(st.reg_date, 'YYYY-MM-DD') AS "reg_date" ,st.content as "content" ,st.stadium_status as "stadium_status"
FROM stadium st LEFT JOIN sido s ON st.sido_name = s.sido_id LEFT JOIN sigungu si ON st.sigungu_name = si.sigungu_id where st.stadium_status != '이용불가'
) zxcvb) where RNUM >= #{begin_rowNo} and RNUM <= #{end_rowNo}

select st.stadium_no AS "stadium_no" ,st.stadium_name AS "stadium_name" ,s.name AS "sido_name" ,si.name as "sigungu_name" ,st.detailed_address as "detail_address" ,to_char(st.reg_date, 'YYYY-MM-DD') AS "reg_date" ,st.content as "content" ,st.stadium_status as "stadium_status"
FROM stadium st LEFT JOIN sido s ON st.sido_name = s.sido_id LEFT JOIN sigungu si ON st.sigungu_name = si.sigungu_id
where stadium_no=#{stadium_no}

mapper.insert:
		insert into booking(

		booking_no
		,stadium_no
		,booking_date
		,time_slot
		,m_no
		)
		values ( (select
		nvl(max(booking_no),0)+1
		from booking)
		,#{stadium_no}
		,#{date}
		,#{time_slot}
		,#{m_no}
		)

mapper.select:
select count(*) from booking
where stadium_no = #{stadium_no} and
time_slot = #{time_slot} and booking_date = #{date}

select * from ( select rownum RNUM, zxcvb.* from (
select sy.yangdo_no AS "yangdo_no", sy.title AS "title", sy.writer AS "writer", m.nickname as "nickname", sy.content AS "content", to_char(sy.reg_date, 'YYYY-MM-DD') AS "reg_date", sy.readcount AS "readcount", sy.booking_no as "booking_no" FROM stadium_yangdo sy join member m on m.m_no = sy.writer JOIN BOOKING B ON sy.BOOKING_NO = B.BOOKING_NO JOIN STADIUM S ON B.STADIUM_NO = S.STADIUM_NO join sido si on s.sido_name = si.sido_id join sigungu sg on s.sigungu_name = sg.sigungu_id where 1=1
) zxcvb) where RNUM >= #{begin_rowNo} and RNUM <= #{end_rowNo}

mapper.update:
update stadium_yangdo set
    title = #{title},
    content = #{content}
   
    , booking_no = (
        select booking_no 
        from booking 
        where stadium_no = #{stadium_no}
          and booking_date = #{booking_date}
          and time_slot = #{time_slot}
          and m_no = #{m_no}
    )
    
where yangdo_no = #{yangdo_no}

mapper.select:
		   select count(*) from stadium_yangdo
		   where  
		   1=1
		   
        	and
        	booking_no = (
            select booking_no 
            from booking 
            where stadium_no = #{stadium_no}
              and booking_date = #{booking_date}
              and time_slot = #{time_slot}
              and m_no = #{m_no}
        )

mapper.update:
    update booking set
        m_no = #{m_no}
       
		where 1=1
		and
        	booking_no = #{booking_no}
